            new UserType(prismContext)
                    .oid('0025b4fd-e4d5-4055-8e65-6eb9a6c56fb2')
                    .name('any'), // just to avoid empty values
            new UserType()
                    .oid('00000000-0000-0000-0000-000000000002')
                    .name('any') // just to avoid empty values
    ]
} else {
    new UserType()
            .oid(test)
            .name('any') // just to avoid empty values
